// Solution with split and reduce
// Part 1

fs.readFile("./santa.txt", "utf8", (err, data) => {
    console.time("second: ");

    const input = data.split("");

    const answer = input.reduce((acc, cur) => {
        if(cur === "(")
            return acc += 1;
        else
            return acc -= 1;
    }, 0);

    console.timeEnd("second: ");
    console.log(answer)
});

// Part 2

fs.readFile("./santa.txt", "utf8", (err, data) => {
    console.time("third: ");

    const input = data.split("");

    let acc = 0;
    let c = 0;

    const answer = input.some(item => {
        if(item === "("){
            acc += 1;
        }
        else{
            acc -= 1;
        }

        c += 1;

        return acc < 0;
    });

    console.timeEnd("third: ");
    console.log("third:", c);
});